let renderCount = 0

type Props = {
  title: string
  logo: ReactElement
  description: string
  color: string
  link: string
  backgroundImage: { light: string; dark: string }
}

const SideProjectCard: React.FC<Props> = ({
  link,
  logo,
  color,
  title,
  description,
  backgroundImage,
}) => {
  const [theme] = useContext(userPreferencesContext).theme
  const bgDiv = useRef(null)
  const hoverEffect: MouseEventHandler<HTMLElement> = (e) => {
    if (!bgDiv.current) return
    const currentTarget = e.currentTarget as HTMLElement
    const rect = currentTarget.getBoundingClientRect()
    const x = e.clientX - rect.left,
      y = e.clientY - rect.top
    const bg = bgDiv.current as HTMLElement
    bg.style.setProperty("--mouse-x", `${x}px`)
    bg.style.setProperty("--mouse-y", `${y}px`)
  }
  renderCount++
  return (
    <Link href={link}>
      <article
        onMouseMove={hoverEffect}
        className={clsx(
          styles.card,
          "relative isolate w-80 cursor-pointer overflow-hidden rounded-lg border border-black/10 bg-zinc-100/50 p-4 hover:border-transparent dark:border-white/10 dark:bg-zinc-800/50 dark:hover:border-transparent"
        )}
        style={{ "--color": color } as CSSProperties}
      >
        <div className="z-50">
          <div
            className={clsx(
              `w-fit rounded-full border border-black/10 bg-black/5 p-2 dark:border-white/10 dark:bg-white/5`,
              styles.icon
            )}
          >
            {logo}
          </div>
          <div className="py-5"></div>
          <div className="lef space-y-1 text-sm">
            <h1>{title}</h1>
            <p
              className={clsx(
                styles.description,
                "text-black/60 dark:text-white/60"
              )}
            >
              {description}
            </p>
          </div>
        </div>
        <div
          className={clsx(
            styles.pattern,
            "absolute top-0 left-0 z-30 h-[150%] w-[150%]"
          )}
          aria-hidden
        >
          <Pattern
            className={clsx("w-full -rotate-6 dark:opacity-70 ", {
              "-ml-16 -mb-5": renderCount % 2 === 0,
            })}
          />
        </div>
        <div
          className={clsx(
            styles.bg,
            "absolute top-0 left-0 z-10 h-[100%] w-[100%]"
          )}
          aria-hidden
          ref={bgDiv}
        >
          <img
            src={`/images/${backgroundImage[theme]}`}
            className="h-[100%] w-[100%] rounded-lg border border-transparent"
          />
        </div>
      </article>
    </Link>
  )
}

export default SideProjectCard
